import { useState } from 'react';
import '../dash.css';


const Dashboard=()=>{

// const postData=[
//     {
//         id:1,
//         first_name:"purus",
//         last_name:"black",
//         address:"jaffna"
//     },
//     {
//         id:2,
//         first_name:"purus",
//         last_name:"black",
//         address:"jaffna"
//     },
//     {
//         id:3,
//         first_name:"purus",
//         last_name:"black",
//         address:"jaffna"
//     },
// ]
const [userData,setUserData]=useState({
    first_name:'',last_name:'',address:''
});
const [dataList,setDataList]=useState([]);

const handleChange=(e)=>{
    const {name,value}=e.target;
    setUserData({...userData,[name]:value});
};

const handleSubmit=(e)=>{
    e.preventDefault();
    if(postData.first_name && postData.last_name && postData.address){
        setDataList([...dataList,{...postData,id:dataList.length+1}]);
        setDataList({first_name:'',last_name:'',address:''});
    }
}
    return (
        <div className="dashboard">
            {/* content */}
            {/* <div className='content'>
                <div class="content-1">
                    <div className='content-header'>
                        <h1>Task </h1>
                    </div>
                    <div className='content-body'>
                        <h1>{postData.length}</h1>
                    </div>
                </div>
                <div class="content-2">
                    <div className='content-header'>
                        <h1>Content 2</h1>
                        
                    </div>
                    <div className='content-body'>
                        <h1>Content 1</h1>
                    </div>
                    
                </div>
            </div> */}
            {/* content-end */}

            {/* task */}
            <div className='task'>
            <h1>Add Users</h1>
                <div>
                    <form onSubmit={handleSubmit}>
                    <label>First name</label>
                    <input type='text' name='first_name' value={postData.first_name} onChange={handleChange}/>
                    <label>Last Name</label>
                    <input type='text' name='last_name' value={postData.last_name} onChange={handleChange}/>
                    <label>Address</label>
                    <input type='text' name='address' value={postData.address} onChange={handleChange}/>
                    <button type='submit'>Submit</button>
                    </form>
                </div>
                 
            </div>
            {/* task_detail */}
            {/* <Table postData={postData}/> */}
            <div className='task-table'>
                <h1>Users</h1>
                <table>
        <tr>
         <th>First Name</th>
        <th>Last Name</th>
        <th>Address</th>
        


        </tr>
        <tbody>
             {dataList.map((item)=>(
                <tr key={item.id}>
                    <td>{item.first_name}</td>
                    <td>{item.last_name}</td>
                    <td>{item.address}</td>
                </tr>
             ))}           
        </tbody>
     </table>

            </div>
            
        </div>
        
    )
}
export default Dashboard;
