import { useState } from 'react';
import '../dash.css';



const Dashboard=()=>{


const [userData,setUserData]=useState({
    first_name:'',last_name:'',address:''
});
const [dataList,setDataList]=useState([]);

const handleChange=(e)=>{
    const {name,value}=e.target;
    setUserData({...userData,[name]:value});
};

const handleSubmit=(e)=>{
    e.preventDefault();
    if(userData.first_name && userData.last_name && userData.address){
        setDataList([...dataList,{...userData,id:dataList.length+1}]);
        setUserData({first_name:'',last_name:'',address:''});
    }
}
    return <>
<div className="container">
            {/* task */}
            
                <h1>Add Users</h1>
                <div>
                    <form onSubmit={handleSubmit}>
                    <div class="form-floating mb-3">
                    <input type="text" className="form-control" name='first_name' id="floatingInput" placeholder="First Name" value={userData.first_name} onChange={handleChange}/>
                    <label for="floatingInput">First Name</label>
                    </div>

                    <div class="form-floating mb-3">
                    <input type="text" className="form-control" name='last_name' id="floatingInput" placeholder="Last Name" value={userData.last_name} onChange={handleChange}/>
                    <label for="floatingInput">Last Name</label>
                    </div>

                    <div class="form-floating mb-3">
                    <input type="text" className="form-control" name='address' id="floatingInput" placeholder="Address" value={userData.address} onChange={handleChange}/>
                    <label for="floatingInput">Address</label>
                    </div>
                    
                    <button className='btn btn-primary' type='submit'>Submit</button>
                    </form>
                </div>
                 
            
            
            <div className='task-table'>
                <h1>Users({dataList.length})</h1>
                <table>
                <tr>
                <th>First Name</th>
                <th>Last Name</th>
                <th>Address</th>
                <th>Action</th>
                </tr>
                <tbody>
                    {dataList.map((item)=>(
                        <tr key={item.id}>
                            <td>{item.first_name}</td>
                            <td>{item.last_name}</td>
                            <td>{item.address}</td>
                            <td><button className='edit'>Edit</button><button className='delete'>Delete</button></td>
                        </tr>
                    ))}           
                </tbody>
            </table>

        </div>
            
</div>
   

</>
}
export default Dashboard;
