import { useState } from "react";

const AddPostForm=(props)=>{
const intialPostState={id:null,username:"",post_name:"",post_type:""}
const [user,setUser]=useState(intialPostState);

const handleInputChange=(event)=>{
    const {name,value}=event.target
    setUser({...user,[name]:[value]})
}

    return (
    <div className="form-container">
            <form onSubmit={
                event=>{
                    event.preventDefault();
                    if(!user.username || !user.post_name || !user.post_type) return;
                    props.addPost(user);
                    setUser(intialPostState);
                    
                }
            }>
                <h2>Add Post</h2>
                <div className="form-group">
                    <label htmlFor="username">Username:</label>
                    <input type="text" name="username" onChange={handleInputChange} value={user.name}/>
                </div>
                <div className="form-group">
                    <label htmlFor="post_name">Post Name:</label>
                    <input type="text" name="post_name" onChange={handleInputChange} value={user.name}/>
                </div>
                <div className="form-group">
                    <label htmlFor="post_type">Post Type:</label>
                    <input type="text" name="post_type" onChange={handleInputChange} value={user.name}/>
                </div>
                <button type="submit">Submit</button>
            </form>
        </div>
        )
}
export default AddPostForm;
