import { useState } from 'react';
import '../dash.css';
import Contact from './Contact';


const Dashboard=()=>{


const [userData,setUserData]=useState({
    first_name:'',last_name:'',address:''
});
const [dataList,setDataList]=useState([]);

const handleChange=(e)=>{
    const {name,value}=e.target;
    setUserData({...userData,[name]:value});
};

const handleSubmit=(e)=>{
    e.preventDefault();
    if(userData.first_name && userData.last_name && userData.address){
        setDataList([...dataList,{...userData,id:dataList.length+1}]);
        setUserData({first_name:'',last_name:'',address:''});
    }
}
    return <>
<div className="dashboard">
            {/* task */}
            <div className='task'>
                <h1>Add Users</h1>
                <div>
                    <form onSubmit={handleSubmit}>
                    <label>First name</label>
                    <input type='text' name='first_name' value={userData.first_name} onChange={handleChange}/>
                    <label>Last Name</label>
                    <input type='text' name='last_name' value={userData.last_name} onChange={handleChange}/>
                    <label>Address</label>
                    <input type='text' name='address' value={userData.address} onChange={handleChange}/>
                    <button type='submit'>Submit</button>
                    </form>
                </div>
                 
            </div>
            
            <div className='task-table'>
                <h1>Users({dataList.length})</h1>
                <table>
                <tr>
                <th>First Name</th>
                <th>Last Name</th>
                <th>Address</th>
                <th>Action</th>
                </tr>
                <tbody>
                    {dataList.map((item)=>(
                        <tr key={item.id}>
                            <td>{item.first_name}</td>
                            <td>{item.last_name}</td>
                            <td>{item.address}</td>
                            <td><button className='edit'>Edit</button><button className='delete'>Delete</button></td>
                        </tr>
                    ))}           
                </tbody>
            </table>

        </div>
            
</div>
   
<Contact/>
</>
}
export default Dashboard;
