import React, { useState } from "react";
import { FaBars, FaPhoneAlt, FaTh, FaUserAlt } from "react-icons/fa";
import { NavLink } from "react-router-dom";
import Nav from "./Nav";


export default function Sidebar({children}) {
const[isOpen,setIsOpen]=useState(true);
const toggle=()=>setIsOpen(!isOpen)    
const menuItem=[
    {
        path:"/",
        name:"Dashboard",
        icon:<FaTh/>
    },
    {
        path:"/about",
        name:"About",
        icon:<FaUserAlt/>
    },
    {
        path:"/contact",
        name:"Contact",
        icon:<FaPhoneAlt/>
    }
]

const postData=[
    {
        id:1,
        username:"purus",
        post_name:"development"
    },
    {
        id:2,
        username:"purus",
        post_name:"development"
    },
    {
        id:3,
        username:"purus",
        post_name:"development"
    }
]
    return <>
    <div className="container">
        <div className="sidebar" style={{width:isOpen?"200px":"50px"}}>
            <div className="top_section">
                <h1 className="logo" style={{display:isOpen?"block":"none"}}>Logo</h1>
                <div className="bars" style={{marginLeft:isOpen?"50px":"0px"}}>
                    <FaBars onClick={toggle}/>
                </div>
            </div>
            {
                menuItem.map((item,index)=>(
                    <NavLink to={item.path} className="link" activeclassName="active" style={{borderRadius:isOpen?"":"30%"}}>
                        <div className="icon">{item.icon}</div>
                        <div className="link_text" style={{display:isOpen?"block":"none"}}>{item.name}</div>
                    </NavLink>
                ))
            }
        </div>
        
        <div className="main-content" style={{marginLeft:isOpen?"200px":"50px"}}>
            <div className="toolbar" >
            <Nav postData={postData}/>
            </div>
            <main>
            {children}
            </main>
        </div>
    </div>

    </>
}
